"//console.log(data.request.query.symbol);
  let symbol=data.request;
  let quoteUrl=`https://finnhub.io/api/v1/quote?symbol=${symbol}`;
  let profileUrl=`https://finnhub.io/api/v1/stock/profile2?symbol=${symbol}`;
  let headers={
    "X-Finnhub-Token": process.env.FINNHUBTOKEN
  };
  return {quoteUrl, profileUrl, headers};"

